import axios from "axios";
import toast from 'react-hot-toast';
import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  AUTH_ERROR,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
  CLEAR_PROFILE
} from "./types";
import setAuthToken from "../utils/setAuthToken";

// Toast inline styles
const toastStyles = {
  success: {
    backgroundColor: 'green',
    color: 'white',
    borderRadius: '0.375rem'
  },
  error: {
    backgroundColor: 'red',
    color: 'white',
    borderRadius: '0.375rem'
  },
  info: {
    backgroundColor: 'blue',
    color: 'white',
    borderRadius: '0.375rem'
  },
  warning: {
    backgroundColor: 'yellow',
    color: 'black',
    borderRadius: '0.375rem'
  }
};

export const loadUser = () => async dispatch => {
  if (localStorage.token) {
    setAuthToken(localStorage.token);
  }
  try {
    const res = await axios.get("http://127.0.0.1:8000/api/auth");
    dispatch({ type: USER_LOADED, payload: res.data });
  } catch (err) {
    dispatch({ type: AUTH_ERROR });
  }
};

// Register User
export const register = ({ name, email, password }) => async dispatch => {
  const config = {
    headers: { "Content-Type": "application/json" }
  };

  const body = JSON.stringify({ name, email, password });

  try {
    const res = await axios.post("http://127.0.0.1:8000/api/users", body, config);

    dispatch({
      type: REGISTER_SUCCESS,
      payload: res.data.token
    });

    dispatch(loadUser());
    toast.success("Registration successful!", {
      style: toastStyles.success
    });
  } catch (err) {
    const errors = err.response.data;

    if (errors["name"]) {
      errors["name"].forEach(msg =>
        toast.error(`Name: ${msg}`, {
          style: toastStyles.error
        })
      );
    }

    if (errors["email"]) {
      errors["email"].forEach(msg =>
        toast.error(`Email: ${msg}`, {
          style: toastStyles.error
        })
      );
    }

    if (errors["password"]) {
      errors["password"].forEach(msg =>
        toast.error(`Password: ${msg}`, {
          style: toastStyles.error
        })
      );
    }

    dispatch({
      type: REGISTER_FAIL
    });
  }
};

