import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addComment } from "../../actions/post";

const PostForm = ({ postId }) => {
  const [formData, setFormData] = useState({ text: "" });
  const dispatch = useDispatch();

  const handleChange = e => {
    setFormData({ text: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    dispatch(addComment(postId, formData));
    setFormData({ text: "" });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <div className="bg-blue-500 text-white p-3 rounded-t-lg">
        <h3 className="text-lg font-semibold">Leave A Comment</h3>
      </div>
      <form className="mt-4" onSubmit={handleSubmit}>
        <textarea
          name="text"
          cols="30"
          rows="5"
          placeholder="Comment on this post"
          value={formData.text}
          onChange={handleChange}
          required
          className="w-full p-3 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
        ></textarea>
        <button
          type="submit"
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-md shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-300"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default PostForm;
